model.otd.VW_LIQUIDS_SHIPMENT.raw_sql:
{{ config(materialized='view' ) }}

with SRC_LIKP as (

    select * from {{ source('SAPG9', 'LIKP') }}

),
SRC_OIGSI as (

    select * from {{ source('SAPG9', 'OIGSI') }}

),
SRC_OIGS as (

    select * from {{ source('SAPG9', 'OIGS') }}

)

SELECT
    l.VBELN
    ,oi.SHNUMBER AS SHIPMENT_NUMBER
    ,oi.SHNUMBER AS MARINE_SHIPMENT
    ,NULL AS FREIGHT_BOOKING
    ,oi.SHNUMBER AS SHIPMENT
    ,ogs.YCDRG_EXT_FREIGHT_ORD AS FREIGHT_ORDER
FROM SRC_LIKP l
    LEFT OUTER JOIN SRC_OIGSI oi ON oi.DOC_NUMBER = l.VBELN and oi.DOC_TYP = 'J'
    LEFT OUTER JOIN SRC_OIGS ogs ON ogs.SHNUMBER = oi.SHNUMBER

