model.otd.VW_SOLIDS_SHIPMENT.raw_sql:
with SRC_LIKP as (

    select * from {{ source('SAPG9', 'LIKP') }}

),
SRC_VTTP as (

    select * from {{ source('SAPG9', 'VTTP') }}

),
SRC_VTTK as (

    select * from {{ source('SAPG9', 'VTTK') }}

)

SELECT
    l.VBELN
    ,vt.TKNUM AS SHIPMENT_NUMBER
    ,IFF( vtk.VSART = '10' ,vtk.TKNUM  , NULL) AS MARINE_SHIPMENT
    ,IFF( vtk.VSART = '10' ,vtk.EXT_FREIGHT_ORD , NULL) AS FREIGHT_BOOKING
    ,IFF( vtk.VSART != '10',vtk.TKNUM , NULL) AS SHIPMENT
    ,IFF( vtk.VSART != '10',vtk.EXT_FREIGHT_ORD , NULL) AS FREIGHT_ORDER
FROM SRC_LIKP l
    LEFT OUTER JOIN  SRC_VTTP vt ON vt.VBELN = l.VBELN
    LEFT OUTER JOIN  SRC_VTTK vtk ON vtk.TKNUM = vt.TKNUM

