##############################################################################
#
#	Common function used for submitting spark jobs
#
##############################################################################
fn_submitToSpark()
{
 JOB_CLASS_NAME="$1"
 JOB_PARAMS="$2"
 CONSOLE_CAPTURE_LOG="$3"
 LOCAL_OR_CLUSTER_MODE="$4"

 echo "Local Or cluster Mode : $LOCAL_OR_CLUSTER_MODE"

 case $LOCAL_OR_CLUSTER_MODE in
   "CLUSTER")
		#Yarn client mode
		SPARK_SUBMIT_CMD="$SPARK_HOME/bin/spark-submit --master yarn-client  --verbose"

		#Yarn cluster mode
		#export SPARK_SUBMIT_CMD="spark-submit --master yarn-cluster"

		#SPARK_SUBMIT_CMD="$SPARK_SUBMIT_CMD --conf spark.driver.memory=2g"
		SPARK_SUBMIT_CMD="$SPARK_SUBMIT_CMD --num-executors=3"

		export SPARK_SUBMIT_CMD
		;;
   *)
		#Client mode
		export SPARK_SUBMIT_CMD="$SPARK_HOME/bin/spark-submit --master local[10] --verbose"
		;;
 esac

 #Generic spark options
 #SPARK_SUBMIT_CMD="$SPARK_SUBMIT_CMD --conf spark.executor.memory=12g"
 SPARK_SUBMIT_CMD="$SPARK_SUBMIT_CMD --conf spark.driver.memory=8g"

 SPARK_SUBMIT_CMD="$SPARK_SUBMIT_CMD --conf spark.ui.enabled=false"
 #SPARK_SUBMIT_CMD="$SPARK_SUBMIT_CMD --conf spark.scheduler.mode=FAIR"
 #SPARK_SUBMIT_CMD="$SPARK_SUBMIT_CMD --conf spark.sql.autoBroadcastJoinThreshold=13107200"
 #SPARK_SUBMIT_CMD="$SPARK_SUBMIT_CMD --conf spark.sql.shuffle.partitions=500"
 SPARK_SUBMIT_CMD="$SPARK_SUBMIT_CMD --driver-class-path $CUSTOM_LIB_HOME/postgresql-9.4.1207.jre7.jar"

 echo $SPARK_SUBMIT_CMD

 START=$(date +%s);
 echo "Submiting job $JOB_CLASS_NAME ..."
 $SPARK_SUBMIT_CMD \
			 --jars "$CUSTOM_LIB_HOME/uber-qazzandra-1.6.0-M3.jar,$APP_LIB_HOME/uber-SDRRecon-$CODE_VERSION.jar" \
			 --files="$APP_HOME/src/main/resources/log4j.properties,$APP_HOME/conf/$APP_ENV/sdrreconAppEnv.conf" \
			 --class "$JOB_CLASS_NAME" \
			 $APP_LIB_HOME/uber-SDRRecon-$CODE_VERSION.jar \
			 $BASE_CMD_LINE_ARGS $JOB_PARAMS 2>&1 | tee -a $CONSOLE_CAPTURE_LOG;test ${PIPESTATUS[0]} -eq 0

 EXIT_CODE=$?

 END=$(date +%s);
 diff=$(($END-$START))
 SubmittedApplicationID=`grep "YarnClientImpl.*Submitted application" $CONSOLE_CAPTURE_LOG | sed 's/.*application //g'`

 echo "******************************************************************************************************"| tee -a $CONSOLE_CAPTURE_LOG
 echo ""                                                                                                      | tee -a $CONSOLE_CAPTURE_LOG
 echo "Time elapsed for $JOB_CLASS_NAME => 0:$(($diff / 60)):$(($diff % 60))" 								  | tee -a $CONSOLE_CAPTURE_LOG
 echo ""                                                                                                      | tee -a $CONSOLE_CAPTURE_LOG
 echo "COMMAND Line parameters: $JOB_CLASS_NAME $BASE_CMD_LINE_ARGS $JOB_PARAMS"                              | tee -a $CONSOLE_CAPTURE_LOG
 echo ""                                                                                                      | tee -a $CONSOLE_CAPTURE_LOG
 echo "	Captured logs @ $CONSOLE_CAPTURE_LOG"                                                                 | tee -a $CONSOLE_CAPTURE_LOG
 echo ""                                                                                                      | tee -a $CONSOLE_CAPTURE_LOG
 echo " spark submit exit code : ${EXIT_CODE}"                                                                | tee -a $CONSOLE_CAPTURE_LOG
 echo ""                                                                                                      | tee -a $CONSOLE_CAPTURE_LOG
 echo "	command for investigation : yarn logs -applicationId $SubmittedApplicationID    "                     | tee -a $CONSOLE_CAPTURE_LOG
 echo ""                                                                                                      | tee -a $CONSOLE_CAPTURE_LOG
 echo "******************************************************************************************************"| tee -a $CONSOLE_CAPTURE_LOG

 if [ ${EXIT_CODE} -ne 0 ]
 then
    exit 1
 fi
}

JOB_CLASS_NAME=ca.effacious.learn.gcp.spark.WikiLinkAppMain

#
# The common script has already set common parameters that, if this needs to be overriden redefine it here
#

CONSOLE_CAPTURE_LOG="$APP_LOG_DIR/${DAG_TASK_NAME}_${ASOF_DATE}.log"

rm -rf $CONSOLE_CAPTURE_LOG

fn_submitToSpark $JOB_CLASS_NAME "$CMD_LINE_ARGS" "$CONSOLE_CAPTURE_LOG" #"CLUSTER"

echo " OUTPUT_DATA_DIR : $OUTPUT_DATA_DIR "

